            <node ID="2tp7rga4edsnc8knkjtldq3krq" TEXT="Action">
                <node FOLDER="true" ID="121pb2i3nsqv72jhd1j5fnpfqj" TEXT="Peraturan Umum">
                    <node BACKGROUND_COLOR="#F2F2F2" FOLDER="true" ID="4jeoas5ofqa5s9ha7krmsmimkk" TEXT="*Peraturan Umum Astralaga 2023*&#10;&#10;*Ketentuan Peserta*&#10;1. Sehat jasmani rohani.&#10;2. Tertulis di dalam daftar peserta.&#10;3. Mewakili club atau perseorangan.&#10;4. Wajib memakai pakaian tradisional meriah (dilarang menggunakan Jersey atau kaos Klub).&#10;5. Bersepatu.&#10;">
                        <font NAME="SansSerif" SIZE="10"/>
                    </node>
